import React, { useEffect } from "react";

function UserInfo(props) {
    var username = null
    var localUsername = localStorage.getItem('username')
    var sessionUsername = sessionStorage.getItem('username')
    var urlString = "http://localhost:8080/api/v1/user/" + props.postId
    var canvas = document.getElementById('canvas')
    var ctx = canvas.getContext('2d')
    var chartColors = ["green", "yellow", "grey"]
    var chartValues = []
    var chartLabels = ["Completed", "Attempted", "Not attempted"]

    function createChart(cx, cy, radius, arcWidth, values, colors, labels, selectedValue) {
        var tot=0;
        var accum=0;
        var PI=Math.PI;
        var PI2=PI*2;
        var offset=-PI/2;
        ctx.lineWidth=arcwidth;
        for(var i=0;i<values.length;i++){tot+=values[i];}
        for(var i=0;i<values.length;i++){
            ctx.beginPath();
            ctx.arc(cx,cy,radius,
                offset+PI2*(accum/tot),
                offset+PI2*((accum+values[i])/tot)
            );
            ctx.strokeStyle=colors[i];
            ctx.stroke();
            accum+=values[i];
        }
        var innerRadius=radius-arcwidth-3;
        ctx.beginPath();
        ctx.arc(cx,cy,innerRadius,0,PI2);
        ctx.fillStyle=colors[selectedValue];
        ctx.fill();
        ctx.textAlign='center';
        ctx.textBaseline='bottom';
        ctx.fillStyle='white';
        ctx.font=(innerRadius)+'px verdana';
        ctx.fillText(values[selectedValue],cx,cy+innerRadius*.9);
        ctx.font=(innerRadius/4)+'px verdana';
        ctx.fillText(labels[selectedValue],cx,cy-innerRadius*.25);
    }

    createChart(150, 150, 125, 25, chartValues, chartColors, chartLabels, 0)

    if(localUsername != null) {
        username = localUsername
    }
    if(sessionUsername != null) {
        username = sessionUsername
    }

    useEffect(() => {
        fetch(urlString)
          .then(response => response.json().then(function(json) {
              if(username === json.username)
                document.getElementById('edit-button').hidden = false
          }));
      }, [])

    return (
        <div style={{backgroundColor: "#282c34", color: "white", marginLeft: "20%", width:"100%", display: "flex", flexDirection: "column"}}>
            <div>
                <h1 style={{float: "left", marginLeft: "3%"}}>{props.postId}</h1>
                <button id="edit-button" hidden="true" style={{float:"right", margin:"10px"}}>Edit profile</button>
            </div>
            <div>
                <canvas id="canvas" style={{float: "left", marginLeft: "3%"}}>Test</canvas>
            </div>
        </div>
    );
}

export default UserInfo;
