import React from "react";
import "./QuestionList.css";

export default class QuestionsList extends React.Component {
    state = {
        loading: true,
        questions: null
    }

    async componentDidMount() {
        const FETCH_URL = "http://localhost:8080/api/v1/question"

        const response = await fetch(FETCH_URL)

        const fetchedData = await response.json()

        const data = []

        for(let value of fetchedData) {
            var obj = {Title: value.name, Difficulty: value.difficulty, Author: value.author}
            data.push(obj)
        }

        this.setState({questions: data, loading: false})
    }

    render() {

        var currentPage = 1

        function click(item) {
            currentPage = item
            console.log(currentPage)
        }

        return(
            <table cellSpacing="0" className='question-table' style={{width: '80%', height: 'auto', padding: '10px 10px'}}>
                <thead className='question-table-header'>
                    <tr>
                        <th>Title</th>
                        <th>Difficulty</th>
                        <th>Author</th>
                    </tr>
                </thead>
                <tbody>
                    { this.state.loading ? <div>loading...</div> :
                    Object.values(this.state.questions).map((obj, index) => (
                        <tr key={index} style={(index % 2 == 0) ? {"background-color": "#14161a", height: '100px'} : {height: '100px'}}>
                            {
                                Object.values(obj).map((value, index2) => (
                                        <td key={index2}>
                                            { 
                                                (index2 % 3 == 0) ? <a className="question-table-links" href={"/question?name=" + value}>{value}</a> : value 
                                            }
                                        </td>
                                ))
                            }
                        </tr>
                    ))
                    }
                </tbody>
                <div>
                    <ul className='pagination'>
                        <a>{'<'}</a>
                        { this.state.loading ? <li></li> : 
                            Object.values(this.state.questions).map((obj,index) => (
                                (index % 2 == 0) && <li onClick={click(1 + index/2)}>{1 + index/2}</li>
                            ))
                        }
                        <li>{'>'}</li>
                    </ul>
                </div>
            </table>
        )
    }
}
