import React from "react";

export default class QuestionsList extends React.Component {
    state = {
        loading: true,
        questions: null
    }

    async componentDidMount() {
        const FETCH_URL = "http://localhost:8080/api/v1/question"

        const response = await fetch(FETCH_URL)

        const fetchedData = await response.json()

        const data = []

        for(let value of fetchedData) {
            var obj = {Title: value.name, Difficulty: value.difficulty, Author: "StrangeQuark"}
            data.push(obj)
        }

        console.log(data)

        this.setState({questions: data, loading: false})
    }

    render() {
        return(
            // <div>
            //     {this.state.loading || !this.state.questions ? <div>loading...</div> : <div><div>{this.state.questions[0].name}</div></div>}
            // </div>
            <table cellSpacing="0" style={{width: 'auto', height: 'auto', padding: '5px 10px'}}>
                <thead>
                    <tr>
                        <th>Title</th>
                        <th>Difficulty</th>
                        <th>Header 3</th>
                    </tr>
                </thead>
                <tbody>
                    { this.state.loading ? <div>loading...</div> :
                    Object.values(this.state.questions).map((obj, index) => (
                        <tr key={index}>
                            {
                                Object.values(obj).map((value, index2) => (
                                        <td key={index2}>{value}</td>
                                ))
                            }
                        </tr>
                    ))
                    }
                </tbody>
            </table>
        )
    }
}
