import React from "react";
import "./QuestionList.css";

export default class QuestionsList extends React.Component {
    state = {
        loading: true,
        questions: null,
        questionsLength: null
    }

    async componentDidMount() {
        console.log(window.location.href.includes("projects"))

        const FETCH_URL = "http://localhost:8080/api/v1/question"

        const response = await fetch(FETCH_URL)

        const fetchedData = await response.json()

        const data = []

        for(let value of fetchedData) {
            var obj = {Title: value.name, Difficulty: value.difficulty, Author: value.author}
            data.push(obj)
        }

        this.setState({questions: data, loading: false, questionsLength: data.length})

        //Initialize the first button to be highlighted, since click on the home page will always load the first page of the questionList
        document.getElementById('pagination-button-1').style.color = 'green'
    }

    render() {

        var currentPage = 1
        var numOfPaginationButtons = Math.floor(1 + this.state.questionsLength/2)

        function click(item) {
            currentPage = item

            resetButtonColors("white")

            document.getElementById('pagination-button-' + item).style.color = 'green'
        }

        function resetButtonColors(color) {
            for(var i = 1; i <= numOfPaginationButtons; i++) {
                document.getElementById('pagination-button-' + i).style.color = color
            }
        }

        return(
            <>
            <table cellSpacing="0" className='question-table' style={{width: '80%', height: 'auto', padding: '10px 10px'}}>
                <thead className='question-table-header'>
                    <tr>
                        <th><button className="no-background-button">Title</button></th>
                        <th><button className="no-background-button">Difficulty</button></th>
                        <th><button className="no-background-button">Author</button></th>
                    </tr>
                </thead>
                <tbody>
                    { this.state.loading ? <div>loading...</div> :
                    Object.values(this.state.questions).map((obj, index) => (
                        <tr key={index} style={(index % 2 === 0) ? {"background-color": "#14161a", height: '100px'} : {height: '100px'}}>
                            {
                                Object.values(obj).map((value, index2) => (
                                        <td key={index2}>
                                            { 
                                                (index2 % 3 === 0) ? <a className="question-table-links" href={"/question?name=" + value}>{value}</a> : value 
                                            }
                                        </td>
                                ))
                            }
                        </tr>
                    ))
                    }
                </tbody>
            </table>
            <ul className='pagination'>
                <li><button className="no-background-button" onClick={() => {(currentPage === 1) ? click(currentPage) : click(currentPage - 1)}}>{'<'}</button></li>
                { this.state.loading ? <li></li> : 
                    Object.values(this.state.questions).map((obj,index) => (
                        (index % 2 === 0) && <li><button id={"pagination-button-" + (1 + index/2)} className="no-background-button" onClick={() => {click(1 + index/2)}}>{1 + index/2}</button></li>
                    ))
                }
                <li><button className="no-background-button" onClick={() => {(currentPage === numOfPaginationButtons) ? click(currentPage) : click(currentPage + 1)}}>{">"}</button></li>
            </ul>
        </>
        )
    }
}
