import React, { useState } from "react";
import "./QuestionList.css";

export default class QuestionsList extends React.Component {
    state = {
        loading: true,
        questions: null,
        questionsLength: null
    }

    async componentDidMount() {
        // const FETCH_URL = "http://localhost:8080/api/v1/question"

        // const response = await fetch(FETCH_URL)

        // const fetchedData = await response.json()

        // const data = []

        // for(let value of fetchedData) {
        //     var obj = {Title: value.name, Difficulty: value.difficulty, Author: value.author}
        //     data.push(obj)
        // }

        // this.setState({questions: data, loading: false, questionsLength: data.length})

        // //Initialize the first button to be highlighted, since click on the home page will always load the first page of the questionList
        // document.getElementById('pagination-button-1').style.color = 'green'
    }

    render() {
        function loginHandler(username, password) {
            var loginJSON = {"username": username, "password": password}

            fetch('http://localhost:8080/api/v1/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(loginJSON)
                }).then(response => response.text().then(
                    function (text) {
                        console.log(text)
                        if(text) {
                            localStorage.setItem('username', username)
                            console.log(localStorage.getItem('username') + " has logged in")
                        }
                        else {
                            console.log("login was unsuccessful")
                        }
                    }
                ))
        }

        return(
            <div className="login-div">
                <form id="login-form">
                    <label for="username">Username:</label><br />
                    <input type="text" id="username" name="username" /><br />
                    <label for="password">Password:</label><br />
                    <input type="password" id="password" name="password" /><br />
                </form>
                <button onClick={() => loginHandler(window.document.getElementById('username').value, window.document.getElementById('password').value)}>submit</button>
            </div>
        )
    }
}
